mapper.select:
SELECT i.softwareName,i.APKName,i.softwareSize, i.flatformId, (SELECT valueName FROM data_dictionary d WHERE i.flatformId=d.valueId AND typeCode='APP_FLATFORM') AS flatformName, i.categoryLevel1, (SELECT categoryName FROM app_category c WHERE c.id=i.categoryLevel1) AS categoryLevel1Name, i.categoryLevel2, (SELECT categoryName FROM app_category c WHERE c.id=i.categoryLevel2) AS categoryLevel2Name, i.categoryLevel3, (SELECT categoryName FROM app_category c WHERE c.id=i.categoryLevel3) AS categoryLevel3Name, i.status, (SELECT valueName FROM data_dictionary d WHERE i.status=d.valueId AND typeCode='APP_STATUS') AS statusName, i.downloads, i.versionId, (SELECT versionNo FROM app_version v WHERE i.versionId=v.id) AS versionNo FROM app_info i
and i.softwareName like CONCAT ('%',#{softwareName},'%')
and i.status=#{status}
and i.flatformId=#{flatformId}
and i.categoryLevel1=#{categoryLevel1}
and i.categoryLevel2=#{categoryLevel2}
and i.categoryLevel3=#{categoryLevel3}
LIMIT #{start},#{pageSize}

SELECT COUNT(1) FROM app_info i

	
		and i.softwareName like CONCAT ('%',#{softwareName},'%')
	
	
		and i.status=#{status}
	
	
		and i.flatformId=#{flatformId}
	
	
		and i.categoryLevel1=#{categoryLevel1}
	
	
		and i.categoryLevel2=#{categoryLevel2}
	
	
		and i.categoryLevel3=#{categoryLevel3}

select * from app_info
where status=1
limit ${(page.currentPageNo-1)*page.pageSize},${page.pageSize}
